member.example:
다음 예제 메서드에서는 SystemEnvironment를 사용하여 설치여부, 버전, 상태여부의 결과를 출력합니다.

using System;
using System.Collections.Generic;
using System.Text;
using HIE.Framework.Diagnostics;

class SystemEnvironmentSample
{
    static void Main()
    {
        // 운영체제 버전정보를 출력
        Console.WriteLine(HIE.Framework.Diagnostics.SystemEnvironment.GetOSInfo());
        // IIS 실행여부, 버전, 상태여부를 출력
        Console.WriteLine(HIE.Framework.Diagnostics.SystemEnvironment.GetSystemStatus(ServicesType.IIS));
        Console.WriteLine(HIE.Framework.Diagnostics.SystemEnvironment.GetSystemVersion(ServicesType.IIS));
        Console.WriteLine(HIE.Framework.Diagnostics.SystemEnvironment.GetSystemState(ServicesType.IIS));
        // ASPNET 실행여부, 버전, 상태여부를 출력
        Console.WriteLine(HIE.Framework.Diagnostics.SystemEnvironment.GetSystemStatus(ServicesType.ASPNET));
        Console.WriteLine(HIE.Framework.Diagnostics.SystemEnvironment.GetSystemVersion(ServicesType.ASPNET));
        Console.WriteLine(HIE.Framework.Diagnostics.SystemEnvironment.GetSystemState(ServicesType.ASPNET));
        // MSSQL 실행여부, 버전, 상태여부를 출력
        Console.WriteLine(HIE.Framework.Diagnostics.SystemEnvironment.GetSystemStatus(ServicesType.MSSQL));
        Console.WriteLine(HIE.Framework.Diagnostics.SystemEnvironment.GetSystemVersion(ServicesType.MSSQL));
        Console.WriteLine(HIE.Framework.Diagnostics.SystemEnvironment.GetSystemState(ServicesType.MSSQL));
    }
}
